import { Component } from '@angular/core';

@Component({
  selector: 'app-button',
  templateUrl: './button.component.html',
  styleUrls: ['./button.component.css']
})
export class ButtonComponent {
  fontColor = 'blue';
  sayHelloId = 1;
  canClick = true;
  message = "Hello World";


  sayMessage()
  {
    alert(this.message)
  }
}


<button
  type="button"
  [disabled]="!canClick"
  (click)="sayMessage()">
  Trigger alert message
</button>
